    private void btnDivActionPerformed(java.awt.event.ActionEvent evt) {                                       
        // TODO add your handling code here:
        int n1 = Integer.parseInt(txt1.getText());
        int n2 = Integer.parseInt(txt2.getText());
        float result = n1/n2;
        lbld.setText(Float.toString(result));
    }                                      

    private void btnSomaActionPerformed(java.awt.event.ActionEvent evt) {                                        
        // TODO add your handling code here:
        int n1 = Integer.parseInt(txt1.getText());
        int n2 = Integer.parseInt(txt2.getText());
        int result = n1+n2;
        lbld.setText(Integer.toString(result));
    }                                       

    private void btnSubActionPerformed(java.awt.event.ActionEvent evt) {                                       
        // TODO add your handling code here:
        int n1 = Integer.parseInt(txt1.getText());
        int n2 = Integer.parseInt(txt2.getText());
        int result = n1-n2;
        lbld.setText(Integer.toString(result));
    }                                      

    private void btnMultActionPerformed(java.awt.event.ActionEvent evt) {                                        
        // TODO add your handling code here:
        int n1 = Integer.parseInt(txt1.getText());
        int n2 = Integer.parseInt(txt2.getText());
        int result = n1*n2;
        lbld.setText(Integer.toString(result));
    }                                       

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        float n1 = Integer.parseInt(txt1.getText());
        float result = (float) Math.sqrt(n1);
        lbld.setText(Float.toString(result));
    }                                        

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        int n1 = Integer.parseInt(txt1.getText());
        int n2 = Integer.parseInt(txt2.getText());
        int result = (int) Math.pow(n1, n2);
        lbld.setText(Integer.toString(result));
    }                                        

